    <script>
        $('.counter').each(function() {
            var $this = $(this),
                countTo = $this.attr('data-count');

            $({
                countNum: $this.text()
            }).animate({
                    countNum: countTo
                },

                {

                    duration: 8000,
                    easing: 'linear',
                    step: function() {
                        $this.text(Math.floor(this.countNum));
                    },
                    complete: function() {
                        $this.text(this.countNum);
                        //alert('finished');
                    }

                });

        });
    </script>
    <!-- timer -->
    <script>
        // Set initial time in seconds (7 hours, 5 minutes, 15 seconds)
        let timeInSeconds = (7 * 3600) + (5 * 60) + 15;

        function updateTimerDisplay() {
            // Calculate hours, minutes, and seconds
            const hours = Math.floor(timeInSeconds / 3600);
            const minutes = Math.floor((timeInSeconds % 3600) / 60);
            const seconds = timeInSeconds % 60;

            // Format the time as HH:MM:SS
            const formattedTime =
                String(hours).padStart(2, '0') + ":" +
                String(minutes).padStart(2, '0') + ":" +
                String(seconds).padStart(2, '0');

            // Clear the timer display and add each character separately
            const timerDiv = document.getElementById("timer");
            timerDiv.innerHTML = ""; // Clear previous display

            // Loop through each character and create a span for it
            for (const char of formattedTime) {
                const span = document.createElement("span");

                if (char === ":") {
                    span.className = "colon"; // Apply colon style
                    span.textContent = char;
                } else {
                    span.className = "number"; // Apply number style with green background
                    span.textContent = char;
                }

                timerDiv.appendChild(span);
            }
        }

        function countdown() {
            if (timeInSeconds > 0) {
                timeInSeconds--;
                updateTimerDisplay();
            } else {
                clearInterval(timerInterval);
                alert("Time's up!");
            }
        }

        // Initialize the display and start the countdown
        updateTimerDisplay();
        const timerInterval = setInterval(countdown, 1000);
    </script>
    <!-- img change -->
    <script>
        function changeImage(element) {
            // Change the main image source
            const mainImage = document.getElementById("mainImage");
            mainImage.src = element.src;

            // Highlight the selected thumbnail
            const thumbnails = document.querySelectorAll(".product-thumbnail");
            thumbnails.forEach((thumbnail) => thumbnail.classList.remove("active"));
            element.classList.add("active");
        }

        function updateQuantity(change) {
            const quantityInput = document.getElementById("quantityInput");
            let currentQuantity = parseInt(quantityInput.value) || 0;
            currentQuantity += change;

            // Ensure quantity is at least 1
            if (currentQuantity < 1) currentQuantity = 1;

            quantityInput.value = currentQuantity;
        }
    </script>

